import React , {Component} from 'react';
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImangeLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import './App.css';
import ParticlesBg from 'particles-bg'

const returnClarifaiRequestOptions = (imageUrl) =>{
  const PAT = 'b2ae913fa5e549c8a521f54733f75092';
  const USER_ID = 'methis23';   
  const APP_ID = 'my-first-application';
  const IMAGE_URL = imageUrl;    

  const raw = JSON.stringify({
    "user_app_id": {
        "user_id": USER_ID,
        "app_id": APP_ID
    },
    "inputs": [
        {
            "data": {
                "image": {
                    "url": IMAGE_URL
                }
            }
        }
    ]
  });

  const requestOptions = {
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Authorization': 'Key ' + PAT
    },
    body: raw
  };
  return requestOptions;
}




class App extends Component {
  constructor(){
    super()
    this.state = {
      input:"",
      imageUrl: ""
    }

  }

  onInputChange=(event)=>{
    this.setState({input: event.target.value})
  }
  onButtonSubmit = () =>{
    this.setState({imageUrl:this.state.input})
    fetch("https://api.clarifai.com/v2/models/face-detection/outputs", returnClarifaiRequestOptions(this.state.input) )
      .then(response => response.json())

  }

  render(){
    return (
      <div className="App">
        <ParticlesBg className="particles" color='#ffffff' num={200} type="cobweb" bg={true} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
          onInputChange = {this.onInputChange}
          onButtonSubmit={this.onButtonSubmit}
        />
        <FaceRecognition imageUrl={this.state.input}/>
      </div>
    );
  }

}

export default App;
